import React from 'react';
import Breadcrumbs from '@mui/material/Breadcrumbs';
import Link from '@mui/material/Link';

import { Category } from '../../domain/item';
import './Breadcrumb.scss';

type BreadcrumbProps = {
  categories: Array<Category>;
};

export const Breadcrumb = ({ categories }: BreadcrumbProps): JSX.Element => (
  <Breadcrumbs className="categories-breadcrumb" separator=">" aria-label="breadcrumb">
    {categories.map(category => {
      return (
        <Link underline="hover" key={category.id} className="category">
          {category.name}
        </Link>
      );
    })}
  </Breadcrumbs>
);
